          'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQZXVrrg08B_4evcuFpJkAJYi2Ir-BrzW21xQ&s',
    ),
    ProductModel(
      id: '4',
      name: 'RAM DDR5 32GB Kit',
      price: 3200000,
      image:
          'data:image/jpeg;base64,',
    ),
    ProductModel(
      id: '5',
      name: 'Motherboard Z790',
      price: 4500000,
      image:
          'data:image/jpeg;base64,',
    ),
    ProductModel(
      id: '6',
      name: 'SSD NVMe 1TB',
      price: 1800000,
      image:
          'data:image/jpeg;base64,',
    ),
    ProductModel(
      id: '7',
      name: 'Keyboard Mechanical RGB',
      price: 1200000,
      image:
          'data:image/jpeg;base64,',
    ),
    ProductModel(
      id: '8',
